mapper.insert:

	SELECT max(reply_id) + 1 as reply_id
	     , sysdate as reply_date
	  FROM dt_reply

	INSERT  
	  INTO dt_reply (reply_id,reply,board_id,member_id, 
	  
		  parent_num
	  
	  )
	VALUES (#{replyId}, #{reply}, #{boardId},#{memberId}, 
	
		#{parentNum}
	
	)

mapper.update:
UPDATE dt_reply
   SET reply = #{Reply}
     , reply_update = sysdate
 WHERE reply_id = #{reply_id}
